import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
    const [file, setFile] = useState(null);
    const [metadata, setMetadata] = useState(null);
    const [pdfUrl, setPdfUrl] = useState(null);
    const [password, setPassword] = useState('');

    const handleUpload = async (e) => {
        const file = e.target.files[0];
        setFile(file);
        setMetadata({ name: file.name, size: file.size });
        const formData = new FormData();
        formData.append('file', file);
        formData.append('password', password);

        try {
            const response = await axios.post('http://localhost:5001/upload', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });
            setPdfUrl(response.data.pdfUrl);
        } catch (error) {
            console.error('Error uploading file:', error);
        }
    };

    return (
      <div className="container">
      <h1>Docx to PDF Converter</h1>
      <input type="file" accept=".docx" onChange={handleUpload} />
      {metadata && (
          <div className="metadata">
              <h3>File Metadata</h3>
              <p><strong>Name:</strong> {metadata.name}</p>
              <p><strong>Size:</strong> {metadata.size} bytes</p>
          </div>
      )}
      <input
          type="password"
          placeholder="Set PDF Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
      />
      {pdfUrl && (
          <a href={pdfUrl} target="_blank" rel="noopener noreferrer" download>
              Download PDF
          </a>
      )}
      <footer>Powered by Mihir's Converter</footer>
  </div>
    );
};

export default App;
