import React, { useState } from 'react';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [pdfUrl, setPdfUrl] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (file) {
      const formData = new FormData();
      formData.append('file', file);

      try {
        const response = await fetch('http://localhost:5001/upload', {
          method: 'POST',
          body: formData,
        });

        if (response.ok) {
          const data = await response.json();
          setPdfUrl(data.pdfUrl); // Set the URL of the converted PDF
        } else {
          alert('Error uploading or converting file.');
        }
      } catch (error) {
        console.error('Error:', error);
        alert('An error occurred. Please try again.');
      }
    }
  };

  return (
    <div className="App">
      <h1>Docx to PDF Converter</h1>
      <div className="container">
        <input type="file" onChange={handleFileChange} accept=".docx" />
        {file && (
          <div className="metadata">
            <p>Name: {file.name}</p>
            <p>Size: {file.size} bytes</p>
          </div>
        )}
        <button onClick={handleUpload}>Upload and Convert</button>

        {pdfUrl && (
          <div>
            <a href={pdfUrl} download={file.name.replace('.docx', '.pdf')}>
              Download PDF
            </a>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
